import React from 'react'
import ReactDOM from 'react-dom'
import {button} from 'react-bootstrap'

function registerbox() {
    return <h1>This is register box!</h1>
}

function loginbox() {
    return <h1>This is login box!</h1>
}

function login_button(props) {
    return (
        <button onClick={props.onClick}>
            Login
        </button>
    );
}

function register_button(props) {
    return (
        <button onClick={props.onClick}>
            Back to Registration
        </button>
    )
}

function currentBox(props) {
    const isLoginClicked = props.isLoginClicked;
    if (isLoginClicked) {
        return <loginbox />
    } else {
        return <registerbox />
    }
}

class LandingBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {isLoginClicked: false};
        this.loginbox_handler = this.loginbox_handler.bind(this);
        this.registerbox_handler = this.registerbox_handler.bind(this);
    }

    loginbox_handler() {
        this.setState({isLoginClicked : true});
    }

    registerbox_handler() {
        this.setState({isLoginClicked : false});
    }

    render() {
        if (isLoginClicked) {
            button = <login_button onClick={this.loginbox_handler} />
        } else {
            button = <register_button onClick={this.registerbox_handler} /> 
        }

        return (
            <div>
                <currentBox isLoginClicked={isLoginClicked} />
                {button}
            </div>
        )
    }
}

export default LandingBox;
