import React from 'react';
import {Button, Card, TextInput} from 'react-materialize';
import M from "materialize-css";
import './LandingBox.css';

function RegisterBox(props) {
    const button = props.button;
    return (
        <div class="card box-dim hoverable">
            <div class="overlay input-dim">
            <div class="row">
                <div class="input-field col s6 offset-s3 center-align">
                <i class="large material-icons prefix">email</i>
                <input id="email" type="email" class="validate" required="" aria-required="true"/>
                <label htmlFor="email">Email</label>
            </div>
            </div>
            <div class="row">
                <div class="input-field col s6 offset-s3 center-align">
                <i class="large material-icons prefix">account_circle</i>
                <input id="username" type="text" class="validate" />
                <label htmlFor="username">Username</label>
                </div>
            </div>
            <div class="row">
                <div class="input-field col s6 offset-s3 center-align">
                <i class="large material-icons prefix">lock</i>
                <input id="password" type="password" class="validate" />
                <label htmlFor="password">Password</label>
                </div>
            </div>
            <div class="row">
                <div class="input-field col s6 offset-s3 center-align">
                <i class="large material-icons prefix">check_circle</i>
                <input id="confirm_password" type="password" class="validate" onKeyPress={confirm_password_handler} />
                <label id="confirm_password_label" htmlFor="confirm_password">Confirm Password</label>
                </div>
            </div>
            <div class="input-field col s12 center-align">
                <button class="btn waves-effect waves-light" type="submit" name="action">Submit Registration</button>
                {button}
            </div>
            </div>
        </div>
    );
}

function confirm_password_handler() {
    console.log(document.getElementById('confirm_password'));
    if (document.getElementById('confirm_password') != document.getElementById('password')) {
        document.getElementById('confirm_password_label').style.color = '#FF040E';
    } else {
        document.getElementById('confirm_password_label').style.color = '#6DFC01';
    }
}


function LoginBox(props) {
    const button = props.button;
    return (
        <div class="card box-dim hoverable">
            <div class="overlay input-dim">
            <div class="row">
                <div class="input-field col s6 offset-s3 center-align">
                <i class="large material-icons prefix">account_circle</i>
                <input id="username" type="text" class="validate" />
                <label htmlFor="username">Username</label>
                </div>
            </div>
            <div class="row">
                <div class="input-field col s6 offset-s3 center-align">
                <i class="large material-icons prefix">lock</i>
                <input id="password" type="password" class="validate" />
                <label htmlFor="password">Password</label>
                </div>
            </div>
            <div class="input-field col s12 center-align">
                <button class="btn waves-effect" type="submit" name="action">Log In</button>
                {button}
            </div>
            </div>
        </div>
    );
}

function LoginButton(props) {
    return (
        <button class="btn waves-effect" type="submit" name="action" onClick={props.onClick}>
            Register
            {console.log(props.onClick)}
        </button>
    );
}

function RegisterButton(props) {
    return (
        <Button  onClick={props.onClick}>
            Login
        </Button>
    )
}

function CurrentBox(props) {
    const isloginclicked = props.isloginclicked;
    const button = props.button;
    if (isloginclicked) {
        return <LoginBox button={button}/>
    } else {
        return <RegisterBox button={button}/>
    }
}

class LandingBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {isloginclicked: false};
        this.LoginBox_handler = this.LoginBox_handler.bind(this);
        this.RegisterBox_handler = this.RegisterBox_handler.bind(this);
    }

    componentDidMount() {
        M.updateTextFields();
      }
    
    LoginBox_handler() {
        this.setState({isloginclicked : false});
    }

    RegisterBox_handler() {
        this.setState({isloginclicked : true});
    }

    render() {
        const isloginclicked = this.state.isloginclicked;
        let button;

        if (isloginclicked) {
            button = <LoginButton onClick={this.LoginBox_handler} />
        } else {
            button = <RegisterButton onClick={this.RegisterBox_handler} /> 
        }

        return (
            <div>
                <CurrentBox isloginclicked={isloginclicked} button={button} />
            </div>
        )
    }
}

export default LandingBox;
